function n(e=0,i=0,r=0){return e*4+i*4+r*9}function c(e,i){if(!e||!i||i<=0)return{grams:0,calories:0,protein:0,carbs:0,fat:0,fiber:e?.fiber?0:null,sodium:e?.sodium?0:null};const r=i/100,t=(e.protein||0)*r,a=(e.carbs||0)*r,s=(e.fat||0)*r,l=n(t,a,s);return{grams:parseFloat(i.toFixed(2)),calories:parseFloat(l.toFixed(2)),protein:parseFloat(t.toFixed(2)),carbs:parseFloat(a.toFixed(2)),fat:parseFloat(s.toFixed(2)),fiber:e.fiber?parseFloat((e.fiber*r).toFixed(2)):null,sodium:e.sodium?parseFloat((e.sodium*r).toFixed(2)):null}}export{n as calculateCaloriesFromMacros,c as calculateNutrition};
